import React from 'react';
import styles from '../styles/PublicationProcessPage.module.css';

function PublicationProcessPage() {
  const processSteps = [
    {
      number: 1,
      title: 'Manuscript Submission',
      description: 'Authors submit their manuscripts through our online portal. The submission must adhere to our formatting and ethical guidelines. An initial check is performed by the editorial office to ensure the manuscript\'s completeness and suitability for the journal.'
    },
    {
      number: 2,
      title: 'Editorial Assessment',
      description: 'The manuscript is assessed by the editor-in-chief or a designated associate editor to determine if it aligns with the journal\'s scope and meets the required quality standards. Manuscripts that do not meet these criteria may be rejected at this stage.'
    },
    {
      number: 3,
      title: 'Peer Review',
      description: 'Suitable manuscripts are sent to at least two independent expert reviewers in a blind peer-review process. Reviewers provide detailed feedback and recommendations on the manuscript\'s academic rigor, originality, and contribution to the field.'
    },
    {
      number: 4,
      title: 'Decision and Revision',
      description: 'Based on the reviewers\' feedback, the editor makes a decision, which could be acceptance, minor revisions, major revisions, or rejection. If revisions are required, the author is invited to revise and resubmit the manuscript.'
    },
    {
      number: 5,
      title: 'Final Acceptance & Publication',
      description: 'Once the manuscript has successfully passed the peer review and revision stages, it is formally accepted. The manuscript then goes through copyediting, typesetting, and proofreading before being published in the journal.'
    }
  ];

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h2 className={styles.title}>Publication Process</h2>
        <p className={styles.subtitle}>
          Our publication process is designed to ensure the highest quality of scholarly work through a rigorous and transparent review system.
        </p>
      </div>

      <div className={styles.timeline}>
        {processSteps.map((step, index) => (
          <div key={step.number} className={styles.timelineItem}>
            {index < processSteps.length - 1 && (
              <div className={styles.connector}></div>
            )}

            <div className={styles.stepBadge}>{step.number}</div>

            <div className={styles.stepContent}>
              <h3 className={styles.stepTitle}>{step.title}</h3>
              <p className={styles.stepDescription}>{step.description}</p>
            </div>
          </div>
        ))}
      </div>

      <div className={styles.infoSection}>
        <div className={styles.infoCard}>
          <div className={styles.infoIcon}>‚è±Ô∏è</div>
          <h4>Average Timeline</h4>
          <p>Most manuscripts receive a decision within 8-12 weeks from initial submission to final decision.</p>
        </div>

        <div className={styles.infoCard}>
          <div className={styles.infoIcon}>üë•</div>
          <h4>Expert Reviewers</h4>
          <p>We work with leading legal scholars and practitioners from around the world to ensure quality peer review.</p>
        </div>

        <div className={styles.infoCard}>
          <div className={styles.infoIcon}>‚úÖ</div>
          <h4>Quality Assurance</h4>
          <p>Every accepted manuscript undergoes rigorous copyediting and proofreading for publication excellence.</p>
        </div>
      </div>
    </div>
  );
}

export default PublicationProcessPage;
