import React, { useState } from 'react';
import styles from '../../styles/SubmitManuscriptPage.module.css';

function SubmitManuscriptPage() {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    institution: '',
    title: '',
    abstract: '',
    keywords: '',
    manuscript: null,
    authorInfo: null,
    declaration: null,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e, fileType) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, [fileType]: file }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    alert('Manuscript submitted successfully!');
  };

  const downloadDeclaration = () => {
    // Replace with real file path later
    const link = document.createElement('a');
    link.href = '/documents/declaration-form.pdf';
    link.download = 'Declaration_Form.pdf';
    link.click();
  };

  const reviewSteps = [
    { step: 1, title: 'Initial Screening', description: 'Compliance & quality check for formatting and plagiarism.' },
    { step: 2, title: 'Preliminary Evaluation', description: 'Editorial Board assesses thematic relevance and research depth.' },
    { step: 3, title: 'Peer Review', description: 'Double-anonymous evaluation by expert reviewers.' },
    { step: 4, title: 'Review Synthesis', description: 'Consolidated feedback with decision recommendation.' },
    { step: 5, title: 'Author Revision', description: 'Authors revise and provide point-by-point response.' },
    { step: 6, title: 'Final Editorial Review', description: 'Board conducts final consistency check.' },
    { step: 7, title: 'Publication', description: 'Accepted papers formatted and published with DOI.' },
  ];

  return (
    <div className={styles.container}>
      {/* INTRO */}
      <div className={styles.intro}>
        <h2 className={styles.title}>Submit Your Manuscript</h2>
        <p className={styles.introText}>
          The <strong>MLS Law Journal</strong> welcomes original, unpublished manuscripts
          from law students, researchers, and practitioners.
        </p>

        {/* Added Guidelines Button */}
        <button
          className={styles.submitBtn}
          style={{ marginTop: '1rem' }}
          type="button"
        >
          View Submission Guidelines
        </button>
      </div>

      {/* WHO CAN SUBMIT */}
      <div className={styles.whoCanSubmit}>
        <h3 className={styles.sectionHeader}>Who Can Submit?</h3>
        <p className={styles.submitText}>
          <strong>Academicians, Research Scholars, Lawyers, and Law Students</strong> may
          submit original Articles, Book Reviews, Case Comments, and Legislative Notes.
        </p>
      </div>

      {/* FORM */}
      <div className={styles.formSection}>
        <h3 className={styles.formTitle}>Manuscript Submission Form</h3>

        <form onSubmit={handleSubmit} className={styles.form}>
          {/* AUTHOR INFO */}
          <fieldset className={styles.fieldset}>
            <legend className={styles.legend}>Author Information</legend>

            <div className={styles.formGroup}>
              <label className={styles.label}>Full Name</label>
              <input
                name="fullName"
                value={formData.fullName}
                onChange={handleInputChange}
                placeholder="Enter your full name"
                required
                className={styles.input}
              />
            </div>

            <div className={styles.formGroup}>
              <label className={styles.label}>Email Address</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="your.email@example.com"
                required
                className={styles.input}
              />
            </div>

            <div className={styles.formGroup}>
              <label className={styles.label}>Institution / Organization</label>
              <input
                name="institution"
                value={formData.institution}
                onChange={handleInputChange}
                placeholder="Your university or organization"
                className={styles.input}
              />
            </div>
          </fieldset>

          {/* MANUSCRIPT DETAILS */}
          <fieldset className={styles.fieldset}>
            <legend className={styles.legend}>Manuscript Details</legend>

            <div className={styles.formGroup}>
              <label className={styles.label}>Manuscript Title</label>
              <input
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                placeholder="Enter your manuscript title"
                required
                className={styles.input}
              />
            </div>

            <div className={styles.formGroup}>
              <label className={styles.label}>Abstract</label>
              <textarea
                name="abstract"
                value={formData.abstract}
                onChange={handleInputChange}
                placeholder="Enter abstract (150‚Äì250 words)"
                required
                className={styles.textarea}
              />
            </div>

            <div className={styles.formGroup}>
              <label className={styles.label}>Keywords</label>
              <input
                name="keywords"
                value={formData.keywords}
                onChange={handleInputChange}
                placeholder="e.g., Constitutional Law, Rights, Jurisprudence"
                required
                className={styles.input}
              />
            </div>
          </fieldset>

          {/* FILE UPLOADS */}
          <fieldset className={styles.fieldset}>
            <legend className={styles.legend}>Upload Files</legend>

            {/* Manuscript */}
            <div className={styles.fileUpload}>
              <label className={styles.fileLabel}>Manuscript File (.docx / .pdf)</label>
              <input
                type="file"
                accept=".docx,.pdf"
                required
                onChange={(e) => handleFileChange(e, 'manuscript')}
                className={styles.hiddenInput}
                id="manuscriptFile"
              />
              <label htmlFor="manuscriptFile" className={styles.fileButton}>
                üìÑ {formData.manuscript?.name || 'Choose Manuscript'}
              </label>
            </div>

            {/* Author Info */}
            <div className={styles.fileUpload}>
              <label className={styles.fileLabel}>Author Information Form</label>
              <input
                type="file"
                accept=".docx,.pdf"
                required
                onChange={(e) => handleFileChange(e, 'authorInfo')}
                className={styles.hiddenInput}
                id="authorInfoFile"
              />
              <label htmlFor="authorInfoFile" className={styles.fileButton}>
                üë§ {formData.authorInfo?.name || 'Choose Author Info'}
              </label>
            </div>

            {/* Declaration */}
            <div className={styles.fileUpload}>
              <label className={styles.fileLabel}>Declaration / Consent Form</label>

              <button
                type="button"
                onClick={downloadDeclaration}
                className={styles.fileButton}
              >
                ‚¨áÔ∏è Download Declaration
              </button>

              <input
                type="file"
                accept=".docx,.pdf"
                required
                onChange={(e) => handleFileChange(e, 'declaration')}
                className={styles.hiddenInput}
                id="declarationFile"
              />
              <label htmlFor="declarationFile" className={styles.fileButton}>
                ‚úçÔ∏è {formData.declaration?.name || 'Attach Declaration'}
              </label>
            </div>
          </fieldset>

          <button type="submit" className={styles.submitBtn}>
            Submit to MLS Law Journal
          </button>
        </form>
      </div>
    </div>
  );
}

export default SubmitManuscriptPage;
