import React from 'react';
// FIX: Corrected import to assume the CSS file is named .module.css
import styles from '../styles/PublicationPrintPolicy.css'; 

function PublicationPrintPolicy() {
  const contributionTiers = [
    { authorType: 'Single Author', amount: '‚Çπ1500' },
    { authorType: 'Co-Authored (Two Authors)', amount: '‚Çπ1700' },
  ];

  const coverageItems = [
    'The author‚Äôs complimentary hard copy of the published volume',
    'Postage and dispatch costs',
    'Administrative processing for the print edition',
  ];

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        {/* The gold color is applied via the CSS class .title */}
        <h2 className={styles.title}>üìò Publication & Print Policy</h2>
        <p className={styles.subtitle}>
          Details regarding the **free open-access digital publication** and the optional contribution for **hardbound volumes**.
        </p>
      </div>

      {/* --- Digital Publication Section --- */}
      <div className={styles.policySection}>
        <h3>üîì Digital Publication Policy (Open Access)</h3>
        {/* Applied contentBlock class for paragraph styling */}
        <div className={styles.contentBlock}>
          <p>
            Publication in the **MLS Law Journal** is entirely free. **No processing, review, or evaluation fees are charged** at any stage of submission.
          </p>
          <p>
            The digital publication (open-access e-journal) remains **free and accessible** to all readers globally, supporting our mission for wide knowledge dissemination and scholarship accessibility.
          </p>
        </div>
      </div>

      {/* --- Print Contribution Section --- */}
      <div className={styles.policySection}>
        <h3>üì¶ Optional Hardbound Print Policy</h3>
        {/* Applied contentBlock class for paragraph styling */}
        <div className={styles.contentBlock}>
          <p>
            Authors are only required to contribute towards the **printing and dispatch cost of the hardbound volume**, after final acceptance of their manuscript. This contribution is **optional** and is only for those desiring a personal hard copy.
          </p>
        </div>

        {/* --- Contribution Breakdown --- */}
        <h4 className={styles.subHeading}>üí∞ Contribution Breakdown</h4>

        <div className={styles.contributionDetails}>
          
          {/* Contribution Amounts Card */}
          <div className={styles.feesCard}>
            <h4>Contribution Amount (After Acceptance)</h4>
            <ul className={styles.feesList}>
              {contributionTiers.map((item, index) => (
                <li key={index} className={styles.feeItem}>
                  <span>{item.authorType}:</span>
                  <span className={styles.amount}>{item.amount}</span>
                </li>
              ))}
            </ul>
          </div>
          
          {/* Coverage Card */}
          <div className={styles.feesCard}>
            <h4>Inclusion & Coverage</h4>
            <ul className={styles.coversList}>
              {coverageItems.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </div>
        </div>

        <p className={styles.finalPaymentNote}>
          Payment details and instructions will be sent **only after the final acceptance** of the manuscript, along with the acceptance letter and print confirmation form.
        </p>
      </div>

      {/* --- Note Section (Ethics) --- */}
      <div className={styles.noteSection}>
        <p>
          ‚ö†Ô∏è **Note on Ethics:** The payment does **not influence the review or acceptance process**. All submissions are evaluated **solely on academic merit** under the Journal‚Äôs hybrid double-anonymous review policy.
        </p>
      </div>
    </div>
  );
}

export default PublicationPrintPolicy;
