import React, { useState } from "react";
import {
  Sun,
  Moon,
  Menu,
  ChevronDown
} from "lucide-react";
import "../styles/Navbar.css";

export default function Header() {
  const [openDropdown, setOpenDropdown] = useState(null);

  const toggleDropdown = (name) => {
    setOpenDropdown(openDropdown === name ? null : name);
  };

  return (
    <header className="header">
      <div className="headerContent">
        {/* LOGO */}
        <div className="logo">
          <div className="logoShield">MLS</div>
          <span className="logoText">Publications</span>
        </div>

        {/* NAV */}
        <nav className="desktopNav">
          <a className="navLink active">Home</a>
          <a className="navLink">About</a>

          <div className="dropdownContainer">
            <button
              className="navLink"
              onClick={() => toggleDropdown("publications")}
            >
              Publications <ChevronDown size={16} />
            </button>
            {openDropdown === "publications" && (
              <div className="dropdown">
                <a className="dropdownLink">Current Issue</a>
                <a className="dropdownLink">Volume Archive</a>
              </div>
            )}
          </div>

          <a className="navLink">Submission Guidelines</a>

          <div className="dropdownContainer">
            <button
              className="navLink"
              onClick={() => toggleDropdown("info")}
            >
              Information Process <ChevronDown size={16} />
            </button>
            {openDropdown === "info" && (
              <div className="dropdown">
                <a className="dropdownLink">Editorial Board</a>
                <a className="dropdownLink">Peer Review</a>
                <a className="dropdownLink">Publication Process</a>
              </div>
            )}
          </div>

          <div className="dropdownContainer">
            <button
              className="navLink"
              onClick={() => toggleDropdown("policies")}
            >
              Journal Policies <ChevronDown size={16} />
            </button>
            {openDropdown === "policies" && (
              <div className="dropdown">
                <a className="dropdownLink">Ethics Statement</a>
                <a className="dropdownLink">Open Access Policy</a>
              </div>
            )}
          </div>

          <a className="navLink submit">Submit Manuscript</a>
          <a className="navLink">Contact Us</a>
        </nav>

        {/* RIGHT ICON */}
        <div className="rightSection">
          <button className="themeToggle">
            <Moon size={18} />
          </button>
        </div>
      </div>
    </header>
  );
}
