import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { Sun, Moon, ChevronDown } from "lucide-react";
import "../styles/Navbar.css";

function Navbar({ theme, toggleTheme }) {
  const [openDropdown, setOpenDropdown] = useState(null);

  return (
    <header className="navbar">
      <div className="navbarInner">

        {/* LOGO */}
        <Link to="/" className="logo">
          <span className="logoBadge">MLS</span>
          <span className="logoText">Publications</span>
        </Link>

        {/* NAV LINKS */}
        <nav className="navLinks">
          <NavLink to="/" className="navItem">Home</NavLink>
          <NavLink to="/about" className="navItem">About</NavLink>

          {/* DROPDOWN */}
          <div
            className="navDropdown"
            onMouseEnter={() => setOpenDropdown("publications")}
            onMouseLeave={() => setOpenDropdown(null)}
          >
            <span className="navItem dropdownTrigger">
              Publications <ChevronDown size={16} />
            </span>

            {openDropdown === "publications" && (
              <div className="dropdownMenu">
                <NavLink to="/current-issue" className="dropdownItem">
                  Current Issue
                </NavLink>
                <NavLink to="/archives" className="dropdownItem">
                  Archives
                </NavLink>
              </div>
            )}
          </div>

          <NavLink to="/submission-guidelines" className="navItem">
            Submission Guidelines
          </NavLink>

          <NavLink to="/peer-review" className="navItem">
            Information Process
          </NavLink>

          <NavLink to="/journal-policies" className="navItem">
            Journal Policies
          </NavLink>

          <NavLink to="/submit-manuscript" className="navItem submitBtn">
            Submit Manuscript
          </NavLink>
        </nav>

        {/* RIGHT ICON */}
        <button className="themeToggle" onClick={toggleTheme}>
          {theme === "dark" ? <Sun size={18} /> : <Moon size={18} />}
        </button>

      </div>
    </header>
  );
}

export default Navbar;
